let lastScrollY=window.scrollY;const menuIcon=document.querySelector(".menu-icon");window.addEventListener("scroll",(()=>{window.scrollY>lastScrollY?menuIcon.style.opacity="0":menuIcon.style.opacity="1",lastScrollY=window.scrollY}));const scrollToTopButton=document.getElementById("scrollToTop");window.addEventListener("scroll",(()=>{window.scrollY>300?scrollToTopButton.style.display="flex":scrollToTopButton.style.display="none"})),scrollToTopButton.addEventListener("click",(()=>{window.scrollTo({top:0,behavior:"smooth"})}));